
Array ( [status] => Array ( [timems] => 4 [rid] => 6KHKpvsviLkMCtQ4Kw== ) [hits] => Array ( [found] => 909 [start] => 0 [hit] => Array ( [0] => Array ( [id] => sight_2 [fields] => Array ( [category] => Array ( [0] => 購物・商店 [1] => 夜景・觀景塔 ) [update_date] => Array ( [0] => 2021-06-10T05:51:45Z ) [name_tc] => Array ( [0] => 東京晴空塔 (天空樹) Tokyo Skytree ) [description] => Array ( [0] => 又名東京天空樹，顏色以藍白為基調，其上的展望台可將東京景色盡收眼底，於2012年5月22日開幕，是東京的新地標。與押上站連結並有Solamachi購物中心，購物中心集結了時尚品牌、特色餐廳與小吃。Disney store、文具雜貨loft、水果派Qu'il Fait Bon等都在此設店，甚至還有店家推出晴空塔限定的特色商品，如Quil Fait Bon水果派上的晴空塔叉子、廣受歡迎的伴手禮TOKYO BANANA豹紋限定口味等。 ) [area_name] => Array ( [0] => 上野・淺草・兩國・足立 ) [hotfactor] => Array ( [0] => 2171 ) [location] => Array ( [0] => 35.71006269999999,139.8107004 ) [type] => Array ( [0] => sight ) ) [highlights] => Array ( [name_tc] => 東京晴空塔 (天空樹) Tokyo Skytree [description] => Disney store、文具雜貨loft、水果派Qu'il Fait Bon等都在此設店，甚至還有店家推出晴空塔限定的特色商品，如Quil Fait Bon水果派上的晴空塔叉子、廣受歡迎的伴手禮TOKYO BANANA豹紋限定口味等。 [category] => 購物・商店 夜景・觀景塔 [area_name] => 上野・淺草・兩國・足立 [content] => ) ) [1] => Array ( [id] => sight_29 [fields] => Array ( [category] => Array ( [0] => 名勝古蹟 [1] => 交通要地 ) [update_date] => Array ( [0] => 2021-12-09T04:45:01Z ) [name_tc] => Array ( [0] => 東京車站 ) [description] => Array ( [0] => 東京車站於2012年10月3日盛大重新開幕，為包括新幹線在內多條鐵路交會的重要車站。 ) [area_name] => Array ( [0] => 東京車站・銀座・日本橋 ) [hotfactor] => Array ( [0] => 2325 ) [location] => Array ( [0] => 35.681382,139.76608399999998 ) [type] => Array ( [0] => sight ) ) [highlights] => Array ( [name_tc] => 東京車站 [description] => 東京車站於2012年10月3日盛大重新開幕，為包括新幹線在內多條鐵路交會的重要車站。 [category] => 名勝古蹟 交通要地 [area_name] => 東京車站・銀座・日本橋 [content] => ) ) [2] => Array ( [id] => sight_81 [fields] => Array ( [category] => Array ( [0] => 遊樂場・主題樂園 ) [update_date] => Array ( [0] => 2021-08-20T17:15:02Z ) [name_tc] => Array ( [0] => 東京迪士尼樂園 (Tokyo Disney Land) ) [description] => Array ( [0] => 成立於1983年的東京迪士尼樂園位於千葉縣，但從東京出發非常便利，所以仍然是許多人到東京旅遊時的熱門景點。以美國迪士尼的概念建立，加上專屬於東京迪士尼的設施與商品。栩栩如生的童話街景、老少咸宜的遊樂設施、可愛精緻的周邊商品、與熟悉的卡通人物近距離接觸等，是喜歡迪士尼與皮克斯動畫的朋友，到東京不可錯過的夢幻世界。 ) [area_name] => Array ( [0] => 迪士尼 ) [hotfactor] => Array ( [0] => 1992 ) [location] => Array ( [0] => 35.6306579,139.88286949999997 ) [type] => Array ( [0] => sight ) ) [highlights] => Array ( [name_tc] => 東京迪士尼樂園 (Tokyo Disney Land) [description] => 成立於1983年的東京迪士尼樂園位於千葉縣，但從東京出發非常便利，所以仍然是許多人到東京旅遊時的熱門景點。 [category] => 遊樂場・主題樂園 [area_name] => 迪士尼 [content] => ) ) [3] => Array ( [id] => sight_16 [fields] => Array ( [category] => Array ( [0] => 夜景・觀景塔 ) [update_date] => Array ( [0] => 2022-01-27T05:05:01Z ) [name_tc] => Array ( [0] => 東京鐵塔 ) [description] => Array ( [0] => 東京鐵塔位於東京港區芝公園裡，是一座是以巴黎艾菲爾鐵塔為範本而建造的紅白色鐵塔，高333公尺。在晴空塔蓋好之前一直都是東京最知名的高塔地標，也是東京夜景的代表。分別有兩個觀景台：大展望台 (150m) 、特別展望台 (250m)。其對東京的代表性，屢屢出現於書籍、音樂、電影等各種藝術形式中。 ~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~ 2015年3月13日在東京鐵塔 Foot Town內開設航海王/海賊王主題樂園，將結合先前辦展及活動的經驗，搭配上動漫特色餐點、主題遊樂設施，以及週邊販售的形式，創造一座長期經營的主題樂園。而開放的設施裡有布魯克的恐怖城堡、喬巴的千陽號探險以及娜美的賭場等。是航海王/海賊王動漫迷們千萬不能錯過的朝聖景點。 ) [area_name] => Array ( [0] => 赤坂・六本木・麻布・永田町 ) [hotfactor] => Array ( [0] => 1051 ) [location] => Array ( [0] => 35.6586111,139.7455556 ) [type] => Array ( [0] => sight ) ) [highlights] => Array ( [name_tc] => 東京鐵塔 [description] => 東京鐵塔位於東京港區芝公園裡，是一座是以巴黎艾菲爾鐵塔為範本而建造的紅白色鐵塔，高333公尺。 [category] => 夜景・觀景塔 [area_name] => 赤坂・六本木・麻布・永田町 [content] => ) ) [4] => Array ( [id] => restaurant_87473 [fields] => Array ( [category] => Array ( [0] => 咖啡廳、喫茶店 ) [update_date] => Array ( [0] => 2020-08-03T08:30:20Z ) [name_tc] => Array ( [0] => Fuglen Tokyo ) [area_name] => Array ( [0] => 澀谷・青山・惠比壽・目黑 ) [hotfactor] => Array ( [0] => 145 ) [location] => Array ( [0] => 35.6666375,139.6924404 ) [type] => Array ( [0] => restaurant ) ) [highlights] => Array ( [name_tc] => Fuglen Tokyo [description] => [category] => 咖啡廳、喫茶店 [area_name] => 澀谷・青山・惠比壽・目黑 [content] => ) ) [5] => Array ( [id] => hotel_5988 [fields] => Array ( [update_date] => Array ( [0] => 2018-10-08T10:35:02Z ) [name_tc] => Array ( [0] => TOKYO HUTTE ) [de